// Basic Font Awesome JS functionality
(function() {
  'use strict';

  // Basic icon replacement functionality
  var replaceIcons = function() {
    var elements = document.querySelectorAll('i[class*="fa-"]');
    
    elements.forEach(function(element) {
      // Add basic Font Awesome classes if not present
      if (!element.classList.contains('fa') && 
          !element.classList.contains('fas') && 
          !element.classList.contains('far') && 
          !element.classList.contains('fab')) {
        element.classList.add('fas');
      }
    });
  };

  // Run when DOM is ready
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', replaceIcons);
  } else {
    replaceIcons();
  }

  // Watch for dynamically added icons
  var observer = new MutationObserver(function(mutations) {
    mutations.forEach(function(mutation) {
      if (mutation.type === 'childList') {
        replaceIcons();
      }
    });
  });

  observer.observe(document.body, {
    childList: true,
    subtree: true
  });

})();
